import React from "react";
import { TABS } from "../data";
import { TABS_KEYS } from "../data";
import Event from "./Event";

function Main() {
    const ref = React.useRef();
    const [activeTab, setActiveTab] = React.useState('');
    const [hasRightScroll, setHasRightScroll] = React.useState(false);
    const [tempKey, setTempKey] = React.useState([]);
    //const [elementsIndex, setElementsIndex] = React.useState(25);

    React.useEffect(() => {
        if (!activeTab) {
            const newTab = new URLSearchParams(window.location.search).get('tab');
            if (newTab) {
                setActiveTab(newTab);
                setTempKey([newTab]);
            }
            else {
                setActiveTab('all');
                setTempKey(['all']);
            }
        }
    }, []);

    const onSelectInput = event => {
        setActiveTab(event.target.value);
        changeKey(event.target.value);
    };

    const changeKey = (key) => {
        setTempKey([key]);
    }

    let sizes = [];
    const onSize = size => {
        sizes = [...sizes, size];
    };

    React.useEffect(() => {
        const sumWidth = sizes.reduce((acc, item) => acc + item.width, 0);

        const newHasRightScroll = sumWidth > ref.current.offsetWidth;
        if (newHasRightScroll !== hasRightScroll) {
            setHasRightScroll(newHasRightScroll);
        }
    }, [sizes, hasRightScroll]);

    const onArrowCLick = () => {
        const scroller = ref.current.querySelector('.section__panel:not(.section__panel_hidden)');
        if (scroller) {
            scroller.scrollTo({
                left: scroller.scrollLeft + 400,
                behavior: 'smooth'
            });
            //setElementsIndex(prev => prev + 2)
        }
    };

    return <main className="main">
        <section className="section main__general">
            <h2 className="section__title section__title-header section__main-title">Главное</h2>
            <div className="hero-dashboard">
                <div className="hero-dashboard__primary">
                    <h3 className="hero-dashboard__title">Привет, Геннадий!</h3>
                    <p className="hero-dashboard__subtitle">Двери и окна закрыты, сигнализация включена.</p>
                    <ul className="hero-dashboard__info">
                        <li className="hero-dashboard__item">
                            <div className="hero-dashboard__item-title">Дома</div>
                            <div className="hero-dashboard__item-details">
                                +23
                                <span className="a11y-hidden">°</span>
                            </div>
                        </li>
                        <li className="hero-dashboard__item">
                            <div className="hero-dashboard__item-title">За окном</div>
                            <div className="hero-dashboard__item-details">
                                +19
                                <span className="a11y-hidden">°</span>

                                <div
                                    className="hero-dashboard__icon hero-dashboard__icon_rain"
                                    role="img"
                                    aria-label="Дождь"
                                ></div>
                            </div>
                        </li>
                    </ul>
                </div>
                <ul className="hero-dashboard__schedule">
                    <Event
                        icon="temp"
                        iconLabel="Температура"
                        title="Philips Cooler"
                        subtitle="Начнет охлаждать в 16:30"
                    />
                    <Event
                        icon="light"
                        iconLabel="Освещение"
                        title="Xiaomi Yeelight LED Smart Bulb"
                        subtitle="Включится в 17:00"
                    />
                    <Event
                        icon="light"
                        iconLabel="Освещение"
                        title="Xiaomi Yeelight LED Smart Bulb"
                        subtitle="Включится в 17:00"
                    />
                </ul>
            </div>
        </section>

        <section className="section main__scripts">
            <h2 className="section__title section__title-header">Избранные сценарии</h2>

            <ul className="event-grid">
                <Event
                    slim={true}
                    icon="light2"
                    iconLabel="Освещение"
                    title="Выключить весь свет в доме и во дворе"
                />
                <Event
                    slim={true}
                    icon="schedule"
                    iconLabel="Расписание"
                    title="Я ухожу"
                />
                <Event
                    slim={true}
                    icon="light2"
                    iconLabel="Освещение"
                    title="Включить свет в коридоре"
                />
                <Event
                    slim={true}
                    icon="temp2"
                    iconLabel="Температура"
                    title="Набрать горячую ванну"
                    subtitle="Начнётся в 18:00"
                />
                <Event
                    slim={true}
                    icon="temp2"
                    iconLabel="Температура"
                    title="Сделать пол тёплым во всей квартире"
                />
            </ul>
        </section>

        <section className="section main__devices">
            <div className="section__title">
                <h2 className="section__title-header">
                    Избранные устройства
                </h2>

                <select className="section__select" defaultValue="all" onInput={onSelectInput}>
                    {TABS_KEYS.map(key =>
                        <option key={key} value={key}>
                            {TABS[key].title}
                        </option>
                    )}
                </select>

                <ul role="tablist" className="section__tabs">
                    {TABS_KEYS.map(key =>
                        <li
                            key={key}
                            role="tab"
                            aria-selected={key === activeTab ? 'true' : 'false'}
                            tabIndex={key === activeTab ? '0' : undefined}
                            className={'section__tab' + (key === activeTab ? ' section__tab_active' : '')}
                            id={`tab_${key}`}
                            aria-controls={`panel_${key}`}
                            onClick={() => { setActiveTab(key); changeKey(key) }}
                        >
                            {TABS[key].title}
                        </li>
                    )}
                </ul>
            </div>

            <div className="section__panel-wrapper" ref={ref}>
                {tempKey.map(key =>
                    <div key={key} role="tabpanel" className={'section__panel' + (key === activeTab ? '' : ' section__panel_hidden')} aria-hidden={key === activeTab ? 'false' : 'true'} id={`panel_${key}`} aria-labelledby={`tab_${key}`}>
                        <ul className="section__panel-list">
                            {TABS[key].items.map((item, index) =>
                                <Event
                                    key={index}
                                    {...item}
                                    onSize={onSize}
                                />
                            )}
                        </ul>
                    </div>
                )}
                {hasRightScroll &&
                    <div className="section__arrow" onClick={onArrowCLick}></div>
                }
            </div>
        </section>
    </main>;
}

export default Main;
